require './replacement.rb'
require './molecule_calibrator.rb'
require 'pry'

replacements = []
PARSE = /\A(?<start>\w+) => (?<transform>\w+)\z/
IO.read("./input.txt").each_line do |replacement_string|
  match = replacement_string.strip.match(PARSE)
  replacements << Replacement.new(starting_molecule: match[:start],
                                  inserted_molecule: match[:transform])
end

molecule_calibrator = MoleculeCalibrator.new(initial_molecule: "",
                                             replacements: replacements)

puts molecule_calibrator.distinct_molecules
